import React, {Component} from 'react'
import ReactDOM from 'react-dom'
import './React.css'
import FilteredList from './FilteredList.js'
import Cart from './Cart.js'

import Apple from './assets/Apple.png';
import Cherry from './assets/Cherry.png';
import Coconut from './assets/Coconut.png';
import Orange from './assets/Orange.png';
import Peach from './assets/Peach.png';
import Pear from './assets/Pear.png';
import {Paper, Card} from '@material-ui/core'
import 'bootstrap/dist/css/bootstrap.min.css'
import Image from 'react-bootstrap/Image'

const menuList = [
 { name: "Teriyaki Salmon", location: "Andrews", meal: "Dinner", price: "$8.99", image:Apple},
 { name: "Granola Bowl", location: "Andrews", meal: "Breakfast", price: "$6.99", image:Cherry},
 { name: "Pho", location: "Andrews", meal: "Lunch", price: "$7.99", image:Coconut},
 { name: "Overnight Oats", location: "VDub", meal: "Breakfast", price: "$4.99", image:Orange},
 { name: "Tofu Nuggets", location: "VDub", meal: "Lunch", price: "$5.99", image:Peach},
 { name: "Chicken Fingers", location: "VDub", meal: "Lunch", price: "$6.99", image:Pear},
 { name: "Omelette", location: "Ratty", meal: "Breakfast", price: "$6.99", image:Apple},
 { name: "Plantains", location: "Ratty", meal: "Lunch", price: "$4.99", image:Cherry},
 { name: "Taco Tuesday", location: "Ratty", meal: "Lunch", price: "$8.99", image:Coconut},
 { name: "Salad", location: "Jos", meal: "Dinner", price: "$4.99", image:Orange},
 { name: "Mozzarella Sticks", location: "Jos", meal: "Dinner", price: "$3.99", image:Peach},
 { name: "Spicy With", location: "Jos", meal: "Dinner", price: "$5.99", image:Pear},
 { name: "Avocado Toast", location: "IvyRoom", meal: "Dinner", price: "$8.99", image:Apple},
 { name: "Smoothie", location: "IvyRoom", meal: "Breakfast", price: "$6.99", image:Cherry},
 { name: "Naanwich", location: "IvyRoom", meal: "Dinner", price: "$7.99", image:Coconut}
]

class App extends Component {

  constructor() {
    super()
    this.state ={
      items: [],
    }
  }

  addItem = item => {
    const items = [...this.state.items, item]
    this.setState({
      items
    })
  }

  clearItems = () => {
    const items = []
    this.setState({
      items
    })
  }

  render() {
    return(
      <div>
        <Image src="./assets/BDS.png"/>
        <FilteredList addItem={this.addItem} list={menuList}/>
        <Cart items={this.state.items} clear={this.clearItems}/>
      </div>
    )
  }
}

ReactDOM.render(<App />, document.getElementById("root"))
