import React, {Component} from 'react'

import {List,ListItem,Paper} from '@material-ui/core'

import ReactItem from './ReactItem'
import FilteredList from './FilteredList'
import Card from 'react-bootstrap/Card'
import CardDeck from 'react-bootstrap/CardDeck'
import Button from 'react-bootstrap/Button'
import ListGroup from 'react-bootstrap/ListGroup'
import ListGroupItem from 'react-bootstrap/ListGroupItem'

class DisplayList extends Component {
  createItem = item => {
    return (
      <Card id="card" style={{width: '18rem', minWidth: '10rem', textAlign:'center'}}>
        <Card.Img variant="top" src="holder.js/100px180" />
        <ListGroup className="list-group-flush">
          <ListGroupItem>{item.size}</ListGroupItem>
          <ListGroupItem>{item.mood}</ListGroupItem>
        </ListGroup>
        <Card.Body>
          <Card.Title>{item.name}</Card.Title>
          <Card.Text>{item.price}</Card.Text>
          <Button variant="primary">Add</Button>
        </Card.Body>
      </Card>
    )
  }

  render() {
    const itemList = this.props.list
    const mapList = itemList.map(this.createItem)
    return (
      <Paper style={{textAlign:"center"}}>
        <CardDeck style={{maxWidth:"2000px",textAlign:"center", display:"flex", paddingTop :"20px", paddingLeft :"20px", paddingRight :"20px", paddingBottom :"20px"}}>
          {mapList}
            <h2>{mapList.length ? "" : "No results found."}</h2>

        </CardDeck>
      </Paper>
    )
  }
}

export default DisplayList
