import React, {Component} from 'react'

import {List,ListItem} from '@material-ui/core'

import ReactItem from './ReactItem'
import DisplayList from './DisplayList'
import NavBar from 'react-bootstrap/NavBar'
import Nav from 'react-bootstrap/Nav'

class FilteredList extends Component {
  constructor() {
    super()
    this.state ={
      size: "All"
      }
    }


  onSelectFilterSize = event => {
	   this.setState({
		     size: event
	   })
  };

  matchesFilterSize = item => {
  	if(this.state.size === "All") {
  		return true
  	} else if (this.state.size === item.size) {
  		return true
  	} else {
  		return false
  	}
}

  createItem = item => {
    return (
      <ListItem button id="listcont">
        {item.name}
      </ListItem>
    )
  }

  render() {
    return (
      <div>
      <NavBar bg="dark">
        <Nav.Link eventKey="All" onSelect={this.onSelectFilterSize}>All</Nav.Link>
        <Nav.Link eventKey="Desktop" onSelect={this.onSelectFilterSize}>Desktop</Nav.Link>
      </NavBar>
      <DisplayList list={this.props.list.filter(this.matchesFilterSize)}></DisplayList>
      </div>
    )
  }
}

export default FilteredList
