import React, {Component} from 'react'

import {List,ListItem,ListItemText,Paper} from '@material-ui/core'

import Card from 'react-bootstrap/Card'
import CardDeck from 'react-bootstrap/CardDeck'
import Button from 'react-bootstrap/Button'
import ListGroup from 'react-bootstrap/ListGroup'
import ListGroupItem from 'react-bootstrap/ListGroupItem'
var parsePrice = require('parse-price')

class Cart extends Component {
  createTask = item => {
    return (
      <Paper style={{width:"100%", backgroundColor:"white", padding :"10px", margin:"10px"}}>
        <h2>{item.location}</h2>
        <div style={{display:"flex", flexDirection:"row",justifyContent:"space-between"}}>
          <div style={{textAlign:"left"}}>
            <h1>{item.name}</h1>
          </div>
          <div style={{textAlign:"right"}}>
            <h1>{item.price}</h1>
          </div>
        </div>
      </Paper>
    )
  }

  render() {
    const items = this.props.items
    const listItems = items.map(this.createTask)
    const totalPrice = items.reduce(function(accumulator, currentValue) {return Math.round((accumulator + parsePrice(currentValue.price) + Number.EPSILON) * 100) / 100},0)
    return (
      <Paper style={{backgroundColor:"pink", justifyContent:"center", display:"flex", flexDirection:"column", margin:"50px", padding:"50px"}}>
        <h1>Cart</h1>
        <div>{listItems}</div>
        <div style={{textAlign:"center", padding:"10px"}}>
          <h1>Total:</h1>
          <h1>${totalPrice}</h1>
        </div>
        <div>
          {items.length > 0 &&
            <Button variant="success" onClick={this.props.clear} style={{width:"10%", margins:"50px", padding:"10px"}}>Clear Cart</Button>
          }
          {items.length > 0 &&
            <Button variant="success" style={{width:"10%", margins:"50px", padding:"10px"}}>Check Out</Button>
          }
        </div>
      </Paper>
    )
  }
}

export default Cart
