import React, {Component} from 'react'

import {List,ListItem} from '@material-ui/core'

import ReactItem from './ReactItem'
import DisplayList from './DisplayList'
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'

class FilteredList extends Component {
  constructor() {
    super()
    this.state ={
      location: "All",
      meal: "All",
      color11: "red",
      color12: "white",
      color13: "white",
      color14: "white",
      color15: "white",
      color16: "white",
      color21: "red",
      color22: "white",
      color23: "white",
      color24: "white",
      }
    }


  onSelectFilterLocation = event => {
	   this.setState({
		     size: event
	   })

     if(event === "All") {
       this.setState({
  		    color11: "red",
          color12: "white",
          color13: "white",
          color14: "white",
          color15: "white",
          color16: "white",
  	   })
    } else if (event === "Andrews") {
       this.setState({
  		    color11: "white",
          color12: "red",
          color13: "white",
          color14: "white",
          color15: "white",
          color16: "white",
  	   })
    } else if (event === "IvyRoom") {
       this.setState({
  		    color11: "white",
          color12: "white",
          color13: "red",
          color14: "white",
          color15: "white",
          color16: "white",
  	   })
     } else if (event === "Jos") {
        this.setState({
   		     color11: "white",
           color12: "white",
           color13: "white",
           color14: "red",
           color15: "white",
           color16: "white",
   	   })
     } else if (event === "Ratty") {
        this.setState({
           color11: "white",
           color12: "white",
           color13: "white",
           color14: "white",
           color15: "red",
           color16: "white",
        })
      } else {
       this.setState({
  		    color11: "white",
          color12: "white",
          color13: "white",
          color14: "white",
          color15: "white",
          color16: "red",
  	   })
   	}
  };

  onSelectFilterMeal = event => {
	   this.setState({
		     meal: event
	   })

     if(event === "All") {
       this.setState({
  		    color21: "red",
          color22: "white",
          color23: "white",
          color24: "white"
  	   })
    } else if (event === "Breakfast") {
       this.setState({
  		    color21: "white",
          color22: "red",
          color23: "white",
          color24: "white"
  	   })
    } else if (event === "Lunch") {
       this.setState({
  		    color21: "white",
          color22: "white",
          color23: "red",
          color24: "white"
  	   })
     } else {
       this.setState({
  		    color21: "white",
          color22: "white",
          color23: "white",
          color24: "red"
  	   })
   	}
  };

  matchesFilterLocation = item => {
  	if(this.state.location === "All") {
  		return true
  	} else if (this.state.location === item.location) {
  		return true
  	} else {
  		return false
  	}
  }

  matchesFilterMeal = item => {
  	if(this.state.meal === "All") {
  		return true
  	} else if (this.state.meal === item.meal) {
  		return true
  	} else {
  		return false
  	}
  }

  createItem = item => {
    return (
      <ListItem button id="listcont">
        {item.name}
      </ListItem>
    )
  }


  render() {
    return (
      <div>
      <Navbar style={{backgroundColor: "lightsalmon"}}>
        <Navbar.Brand style={{color:"black"}}>Dining Hall:</Navbar.Brand>
        <Nav.Link id="nav-link" style={{color: this.state.color11}} eventKey="All" onSelect={this.onSelectFilterLocation}>All</Nav.Link>
        <Nav.Link id="nav-link" style={{color: this.state.color12}} eventKey="Andrews" onSelect={this.onSelectFilterLocation}>Andrews</Nav.Link>
        <Nav.Link id="nav-link" style={{color: this.state.color13}} eventKey="IvyRoom" onSelect={this.onSelectFilterLocation}>IvyRoom</Nav.Link>
        <Nav.Link id="nav-link" style={{color: this.state.color14}} eventKey="Jos" onSelect={this.onSelectFilterLocation}>Jos</Nav.Link>
        <Nav.Link id="nav-link" style={{color: this.state.color15}} eventKey="Ratty" onSelect={this.onSelectFilterLocation}>Ratty</Nav.Link>
        <Nav.Link id="nav-link" style={{color: this.state.color16}} eventKey="VDub" onSelect={this.onSelectFilterLocation}>VDub</Nav.Link>
      </Navbar>
      <Navbar style={{backgroundColor: "lightsalmon"}}>
        <Navbar.Brand style={{color:"black"}}>Meal:</Navbar.Brand>
        <Nav.Link id="nav-link" style={{color: this.state.color21}} eventKey="All" onSelect={this.onSelectFilterMeal}>All</Nav.Link>
        <Nav.Link id="nav-link" style={{color: this.state.color22}} eventKey="Breakfast" onSelect={this.onSelectFilterMeal}>Breakfast</Nav.Link>
        <Nav.Link id="nav-link" style={{color: this.state.color23}} eventKey="Lunch" onSelect={this.onSelectFilterMeal}>Lunch</Nav.Link>
        <Nav.Link id="nav-link" style={{color: this.state.color24}} eventKey="Dinner" onSelect={this.onSelectFilterMeal}>Dinner</Nav.Link>
      </Navbar>
        <div>
          <DisplayList list={this.props.list.filter(this.matchesFilterLocation).filter(this.matchesFilterMeal)}></DisplayList>
        </div>
      </div>
    )
  }
}

export default FilteredList
