import React, {Component} from 'react'

import {List,ListItem} from '@material-ui/core'

import ReactItem from './ReactItem'
import DisplayList from './DisplayList'
import NavBar from 'react-bootstrap/NavBar'
import Nav from 'react-bootstrap/Nav'

class FilteredList extends Component {
  constructor() {
    super()
    this.state ={
      size: "All",
      mood: "All"
      }
    }


  onSelectFilterSize = event => {
	   this.setState({
		     size: event
	   })
  };

  onSelectFilterMood = event => {
	   this.setState({
		     mood: event
	   })
  };

  matchesFilterSize = item => {
  	if(this.state.size === "All") {
  		return true
  	} else if (this.state.size === item.size) {
  		return true
  	} else {
  		return false
  	}
  }

  matchesFilterMood = item => {
  	if(this.state.mood === "All") {
  		return true
  	} else if (this.state.mood === item.mood) {
  		return true
  	} else {
  		return false
  	}
  }

  createItem = item => {
    return (
      <ListItem button id="listcont">
        {item.name}
      </ListItem>
    )
  }

  linkColor = event => {
    if(event === this.state.size) {
  		return "red"
  	} else {
  		return "white"
  	}
  }

  render() {
    return (
      <div>
      <NavBar bg="dark">
        <Nav.Link id="nav-link" eventKey="All" onSelect={this.onSelectFilterSize}>All</Nav.Link>
        <Nav.Link id="nav-link" eventKey="Desktop" onSelect={this.onSelectFilterSize}>Desktop</Nav.Link>
        <Nav.Link id="nav-link" eventKey="Medium" onSelect={this.onSelectFilterSize}>Medium</Nav.Link>
        <Nav.Link id="nav-link" eventKey="Large" onSelect={this.onSelectFilterSize}>Large</Nav.Link>
      </NavBar>
      <NavBar bg="dark">
        <Nav.Link id="nav-link" style={{backgroundColor: this.linkColor}} eventKey="All" onSelect={this.onSelectFilterMood}>All</Nav.Link>
        <Nav.Link id="nav-link" eventKey="Happy" onSelect={this.onSelectFilterMood}>Happy</Nav.Link>
        <Nav.Link id="nav-link" eventKey="Sad" onSelect={this.onSelectFilterMood}>Sad</Nav.Link>
        <Nav.Link id="nav-link" eventKey="Angry" onSelect={this.onSelectFilterMood}>Angry</Nav.Link>
      </NavBar>
      <DisplayList list={this.props.list.filter(this.matchesFilterSize).filter(this.matchesFilterMood)}></DisplayList>
      </div>
    )
  }
}

export default FilteredList
