import React, {Component} from 'react'

import {List,ListItem,ListItemText,Paper} from '@material-ui/core'

import Card from 'react-bootstrap/Card'
import CardDeck from 'react-bootstrap/CardDeck'
import Button from 'react-bootstrap/Button'
import ListGroup from 'react-bootstrap/ListGroup'
import ListGroupItem from 'react-bootstrap/ListGroupItem'

class Cart extends Component {
  constructor() {
    super()
    this.state ={
      total: "$0.00"
      }
    }

  createTask = item => {
    return (
      <Paper style={{width:"100%", backgroundColor:"white", justifyContent:"center", padding :"10px", margin:"10px"}}>
        <h2>{item.location}</h2>
        <div style={{display:"flex", flexDirection:"row",justifyContent:"space-between"}}>
          <div style={{textAlign:"left"}}>
            <h1>{item.name}</h1>
          </div>
          <div style={{textAlign:"right"}}>
            <h1>{item.price}</h1>
          </div>
        </div>
      </Paper>
    )
  }

  render() {
    const t = 0
    const items = this.props.items
    const listItems = items.map(this.createTask)
    const totalPrice = items.reduce((totalPrice, items) => totalPrice + parseFloat(items.price), 0)
    return (
      <Paper style={{backgroundColor:"pink", justifyContent:"center", display:"flex", flexDirection:"column", margin:"50px", padding:"50px"}}>
        <h1>Cart</h1>
        <div>{listItems}</div>
        <div style={{textAlign:"center", padding:"10px"}}>
          <h2>Total:</h2>
          <h1>{totalPrice}</h1>
        </div>
      </Paper>
    )
  }
}

export default Cart
